import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useForm } from 'react-hook-form';
import Button from '../../components/ui/Button';
import Input from '../../components/ui/Input';
import Card from '../../components/ui/Card';

const SignUpStep2Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, ${props => props.theme.colors.primary} 0%, ${props => props.theme.colors.primaryDark} 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: ${props => props.theme.spacing.xl};
`;

const SignUpCard = styled(motion.div)`
  width: 100%;
  max-width: 600px;
`;

const Logo = styled.div`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xl};
  
  h1 {
    font-size: ${props => props.theme.typography.fontSizes['3xl']};
    font-weight: ${props => props.theme.typography.fontWeights.bold};
    color: ${props => props.theme.colors.primary};
    margin: 0;
  }
  
  p {
    color: ${props => props.theme.colors.textSecondary};
    margin-top: ${props => props.theme.spacing.sm};
  }
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 4px;
  background-color: ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.full};
  margin-bottom: ${props => props.theme.spacing.xl};
  overflow: hidden;
`;

const ProgressFill = styled.div`
  height: 100%;
  background-color: ${props => props.theme.colors.primary};
  width: 66%;
  transition: width ${props => props.theme.transitions.normal};
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: ${props => props.theme.spacing.lg};
`;

const Section = styled.div`
  border-bottom: 1px solid ${props => props.theme.colors.borderLight};
  padding-bottom: ${props => props.theme.spacing.lg};
  margin-bottom: ${props => props.theme.spacing.lg};
  
  &:last-child {
    border-bottom: none;
    padding-bottom: 0;
    margin-bottom: 0;
  }
`;

const SectionTitle = styled.h3`
  font-size: ${props => props.theme.typography.fontSizes.lg};
  font-weight: ${props => props.theme.typography.fontWeights.semibold};
  color: ${props => props.theme.colors.text};
  margin-bottom: ${props => props.theme.spacing.md};
`;

const Row = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${props => props.theme.spacing.lg};
  
  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    grid-template-columns: 1fr;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: ${props => props.theme.spacing.md};
  font-size: ${props => props.theme.typography.fontSizes.base};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.md};
  background-color: ${props => props.theme.colors.white};
  color: ${props => props.theme.colors.text};
  transition: all ${props => props.theme.transitions.normal};
  
  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    box-shadow: 0 0 0 3px ${props => props.theme.colors.primaryLight};
  }
`;

const BudgetRange = styled.div`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.md};
`;

const BudgetInput = styled(Input)`
  flex: 1;
`;

const PreferencesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: ${props => props.theme.spacing.md};
`;

const PreferenceItem = styled.label`
  display: flex;
  align-items: center;
  gap: ${props => props.theme.spacing.sm};
  padding: ${props => props.theme.spacing.md};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.md};
  cursor: pointer;
  transition: all ${props => props.theme.transitions.normal};
  
  &:hover {
    border-color: ${props => props.theme.colors.primary};
    background-color: ${props => props.theme.colors.primaryLight};
  }
  
  input:checked + & {
    border-color: ${props => props.theme.colors.primary};
    background-color: ${props => props.theme.colors.primaryLight};
    color: ${props => props.theme.colors.primary};
  }
`;

const Checkbox = styled.input`
  display: none;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: ${props => props.theme.spacing.md};
  margin-top: ${props => props.theme.spacing.xl};
  
  @media (max-width: ${props => props.theme.breakpoints.sm}) {
    flex-direction: column;
  }
`;

const SignUpStep2 = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = async (data) => {
    setIsLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      console.log('Step 2 data:', data);
      navigate('/confirmation');
    } catch (error) {
      console.error('Step 2 error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleBack = () => {
    navigate('/signup');
  };

  return (
    <SignUpStep2Container>
      <SignUpCard
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <Card>
          <Logo>
            <h1>Wild Welcome</h1>
            <p>Tell us more about yourself</p>
          </Logo>

          <ProgressBar>
            <ProgressFill />
          </ProgressBar>

          <Form onSubmit={handleSubmit(onSubmit)}>
            <Section>
              <SectionTitle>Personal Information</SectionTitle>
              <Row>
                <Input
                  label="Phone Number"
                  type="tel"
                  placeholder="Enter your phone number"
                  {...register('phone', {
                    required: 'Phone number is required',
                    pattern: {
                      value: /^[\+]?[1-9][\d]{0,15}$/,
                      message: 'Invalid phone number',
                    },
                  })}
                  error={errors.phone?.message}
                />
                <Input
                  label="Date of Birth"
                  type="date"
                  {...register('dateOfBirth', {
                    required: 'Date of birth is required',
                  })}
                  error={errors.dateOfBirth?.message}
                />
              </Row>
            </Section>

            <Section>
              <SectionTitle>Location & Preferences</SectionTitle>
              <Row>
                <div>
                  <label>Preferred City</label>
                  <Select {...register('city', { required: 'City is required' })}>
                    <option value="">Select a city</option>
                    <option value="new-york">New York</option>
                    <option value="los-angeles">Los Angeles</option>
                    <option value="chicago">Chicago</option>
                    <option value="houston">Houston</option>
                    <option value="phoenix">Phoenix</option>
                  </Select>
                </div>
                <div>
                  <label>Move-in Date</label>
                  <Input
                    type="date"
                    {...register('moveInDate', {
                      required: 'Move-in date is required',
                    })}
                    error={errors.moveInDate?.message}
                  />
                </div>
              </Row>
            </Section>

            <Section>
              <SectionTitle>Budget & Requirements</SectionTitle>
              <BudgetRange>
                <BudgetInput
                  label="Minimum Budget"
                  type="number"
                  placeholder="Min"
                  {...register('minBudget', {
                    required: 'Minimum budget is required',
                    min: { value: 0, message: 'Budget must be positive' },
                  })}
                  error={errors.minBudget?.message}
                />
                <span style={{ marginTop: '2rem' }}>-</span>
                <BudgetInput
                  label="Maximum Budget"
                  type="number"
                  placeholder="Max"
                  {...register('maxBudget', {
                    required: 'Maximum budget is required',
                    min: { value: 0, message: 'Budget must be positive' },
                  })}
                  error={errors.maxBudget?.message}
                />
              </BudgetRange>
            </Section>

            <Section>
              <SectionTitle>Room Preferences</SectionTitle>
              <PreferencesGrid>
                <div>
                  <Checkbox
                    type="checkbox"
                    id="private-bathroom"
                    {...register('preferences.privateBathroom')}
                  />
                  <PreferenceItem htmlFor="private-bathroom">
                    Private Bathroom
                  </PreferenceItem>
                </div>
                <div>
                  <Checkbox
                    type="checkbox"
                    id="furnished"
                    {...register('preferences.furnished')}
                  />
                  <PreferenceItem htmlFor="furnished">
                    Furnished
                  </PreferenceItem>
                </div>
                <div>
                  <Checkbox
                    type="checkbox"
                    id="parking"
                    {...register('preferences.parking')}
                  />
                  <PreferenceItem htmlFor="parking">
                    Parking
                  </PreferenceItem>
                </div>
                <div>
                  <Checkbox
                    type="checkbox"
                    id="pet-friendly"
                    {...register('preferences.petFriendly')}
                  />
                  <PreferenceItem htmlFor="pet-friendly">
                    Pet Friendly
                  </PreferenceItem>
                </div>
                <div>
                  <Checkbox
                    type="checkbox"
                    id="gym"
                    {...register('preferences.gym')}
                  />
                  <PreferenceItem htmlFor="gym">
                    Gym Access
                  </PreferenceItem>
                </div>
                <div>
                  <Checkbox
                    type="checkbox"
                    id="wifi"
                    {...register('preferences.wifi')}
                  />
                  <PreferenceItem htmlFor="wifi">
                    WiFi Included
                  </PreferenceItem>
                </div>
              </PreferencesGrid>
            </Section>

            <ButtonGroup>
              <Button
                type="button"
                variant="outline"
                onClick={handleBack}
                style={{ flex: 1 }}
              >
                Back
              </Button>
              <Button
                type="submit"
                loading={isLoading}
                style={{ flex: 1 }}
              >
                Complete Setup
              </Button>
            </ButtonGroup>
          </Form>
        </Card>
      </SignUpCard>
    </SignUpStep2Container>
  );
};

export default SignUpStep2;
